//= ============================ Load start external module ===============================//

const Promise = require("bluebird");

//= ====================================== END ===========================================//

//= ================================Load start internal module ============================//

const config = require("../config").env;
const sendGrid = require("sendgrid")(config.sendGrid.apiKey);

//= ======================================== END ==========================================//


//= ============== Implementation ========================//

function sendMail(argument) {
  return new Promise(((resolve, reject) => {
    sendGrid.send(argument, (err, data) => {
      if (err) {
        return reject(err);
      }
      resolve(data);
    });
  }));
}


//= ========================== Exports module ======================//

module.exports = {
  sendMail,
};

//= ============================= END =============================//
