//= ========================= Load Modules Start =======================


//= ========================= Load internal modules ========================================

const PushNotification = require('../model/pushNotification');

// init reward history dao
const BaseDao = new require("../dao/baseDao");
const pushNotificationDao = new BaseDao(PushNotification);

//= ========================= Load Modules End ==============================================

// Method to save push notification
const savePushData = params => {
  const notification = new PushNotification(params);
  return pushNotificationDao.save(notification);
};

//= ==================================== Export module =======================================

module.exports = {
  savePushData,
};

//= ======================================= End =============================================
