//= ========================= Load Modules Start =======================

const mongoose = require("mongoose");

//= ========================= Load internal modules ========================================

const Place = require("../model/places");

// init user dao
const BaseDao = new require("../dao/baseDao");
const appUtils = require("../appUtils");
const appConstants = require("./placesConstants");
const placeDao = new BaseDao(Place);

//= ========================= Load Modules End ==============================================

const createPlaces = (placeInfo) => {
    const place = new Place(placeInfo);
    return placeDao.save(place);
};

//= ==================================== Export module =======================================

module.exports = {
    createPlaces
};

//= ======================================= End =============================================
